require(["jquery"], function ($) {
    $(window).scroll(function () {
        if ($(window).width() > 800) {
            if ($(window).scrollTop() > $(".page-wrapper .page-header").offset().top && !$(".page-wrapper .page-header").hasClass("sticky")) {
                $(".page-wrapper .page-header").addClass("sticky");
            } else if ($(window).scrollTop() == 0) {
                $(".page-wrapper .page-header").removeClass("sticky");
            }
        }
    });
    $(document).ready(function () {
        $(".block.slider-items .product-items").addClass("owl-carousel");
        $(".um-prodslider .content-block.template-type-owl .products-grid .products.list.items").addClass("owl-carousel");
    });
});
